import styled from "styled-components";
import { H3, P } from "components/shared/Dangerously";
import Fade from "react-reveal/Fade";

function DataItem({ item, columns, light, dark }) {
  return (
    <>
      <Column>
        <Fade>
          <Icon light={light} dark={dark}>
            {item.icon}
          </Icon>
        </Fade>
      </Column>
      <ColumnSecond columns={columns}>
        <div />
        <div />
        <H3>{item.title}</H3>
        <P>{item.description}</P>
      </ColumnSecond>
    </>
  );
}

export default DataItem;

const Column = styled.div`
  grid-column-end: span 1;
`;

const Icon = styled.div`
  border-radius: 50%;
  background-color: ${(props) =>
    props.light
      ? props.theme.colors.foreground
      : props.theme.colors.background};
  box-shadow: ${(props) =>
    props.light
      ? `-2px -4px 11px ${props.theme.colors.lightlight},
    2px 2px 4px ${props.theme.colors.lightshadow}`
      : `-5px -5px 7px ${props.theme.colors.darklight}, 
    5px 5px 12px ${props.theme.colors.darkshadow}`
    };
  width: 100%;
  height: 0px;
  padding-bottom: 100%;
  position: relative;
  svg {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate3d(-50%, -50%, 0);
    .mutant{
      fill: ${(props) =>
    props.light
      ? props.theme.colors.background
      : props.theme.colors.foreground};
    }
  }
`;

const ColumnSecond = styled.div`
  div {
    height: 30px;
    width: 30px;
    display:none;
    :nth-of-type(1) {
      background-color: ${(props) => props.theme.colors.darklight};
    }
    :nth-of-type(2) {
      background-color: ${(props) => props.theme.colors.darkshadow};
    }
  }
  grid-column-end: ${(props) => "span " + props.columns};
  h3 {
    font-size: 2.3rem;
    margin-bottom: 17px;
  }
  p {
    color: ${(props) => props.theme.colors.foreground_lowest};
  }
`;
