import { useEffect } from "react";
import styled from "styled-components";
import Fade from "react-reveal/Fade";
import Head from "components/Head";
import FormComplete from "components/FormComplete";
import MainGrid from "components/shared/MainGrid";

function Index(props) {
  return (
    <>
      <Head
        title={"Deep Future Institute"}
        canonical={"https://deepfuture.institute"}
        lang={props.lang}
      />
      <Land id="land">
        <Fade>
          <h1>Genera <b>certidumbre</b> en la crisis económica del COVID-19 y la 4T en México</h1>
          <h2>
            Facilita la toma de <b>decisiones</b> y minimiza el <b>riesgo</b> de
            tu negocio u organización en el <b>futuro</b> con este taller
            autogestivo o guiado por expertos
          </h2>
          <FormComplete centered />
        </Fade>
      </Land>
      <Intro id="removeArrow"></Intro>
    </>
  );
}

export default React.memo(Index);

const Land = styled(MainGrid)`
  min-height: 100vh;
  align-items: center;
  padding-top: 20%;
  h1 {
    color: ${(props) => props.theme.colors.foreground};
    font-size: 4.45rem;
    max-width: 830px;
    line-height: 135%;
    grid-column: 2 / span 8;
  }
  h2 {
    font-size: 2.1rem;
    margin-top: 15px;
    color: ${(props) => props.theme.colors.white};
    grid-column: 2 / span 6;
  }
`;

const LandContainer = styled.div`
  display: flex;
  flex-direction: column;
  grid-column: 2 / span 10;
  @media (max-width: 570px) {
    grid-column: 1 / span 11;
  }
  @media (max-width: 420px) {
    grid-column: 1 / span 12;
  }
`;

const Intro = styled.section`
  color: ${(props) => props.theme.colors.foreground};
  background-color: ${(props) => props.theme.colors.background};
  transition: 0.3s ease all;
`;
