import styled from "styled-components";

function DataItem({ title, description, children }) {
  return (
    <>
      <Column>{children}</Column>
      <ColumnSecond>
        <h3>{title}</h3>
        <p>{description}</p>
      </ColumnSecond>
    </>
  );
}

export default DataItem;

const Column = styled.div`
grid-column-end: span 1;
`;

const ColumnSecond = styled.div`
grid-column-end: span 1;
`;
